# Function to generate a single row with noise
def generate_row(persona):
    row = {}
    for feature, ideal_value in persona.items():
        # Add some noise, ensuring values stay within the 1-5 range (or 1-3 for learning_style)
        noise = random.choice([-1, 0, 0, 1]) # Skew towards no change
        new_value = ideal_value + noise
        
        # Clamp values to their respective ranges
        if feature == 'pref_learning_style':
            new_value = np.clip(new_value, 1, 3)
        else:
            new_value = np.clip(new_value, 1, 5)
        
        row[feature] = new_value
    return row

# Generate the dataset
all_data = []
for career in careers:
    persona = personas[career]
    for _ in range(100): # Generate 100 entries per career
        row_data = generate_row(persona)
        row_data['career'] = career
        all_data.append(row_data)

# Create a pandas DataFrame
df = pd.DataFrame(all_data)

# Save the DataFrame to a CSV file
df.to_csv('career_prediction_dataset.csv', index=False)

print("Dataset successfully generated and saved to 'career_prediction_dataset.csv'")